import { Card } from 'AwesomeCL';

function App() {
  return (
    <Card title="Card Title" description="This is a default card." footer="Footer content here" />
  );
}

import { Card, Image } from 'AwesomeCL';

function App() {
  return (
    <Card 
      title="Card with Image" 
      description="This card has an image at the top."
      imageTop
      image={<Image src='https://example.com/image.jpg' alt='Example Image' />}
    />
  );
}

